using System;
using System.ComponentModel;
using System.IO;
using Xamarin.Forms;

namespace DevExpressGrid.Domain {
    class EditorViewModel: INotifyPropertyChanged {
        private const string PLACEHOLDER = "";

        public event PropertyChangedEventHandler PropertyChanged;

        private string imageResource = PLACEHOLDER;
        public ImageSource ImageSrc {
            get {
                return ImageSource.FromStream(() => {
                    byte[] bytes = Convert.FromBase64String(this.imageResource);
                    return new MemoryStream(bytes);
                });
            }
        }

        public void SetImageResource(string path) {
            byte[] bytes = File.ReadAllBytes(path);
            this.imageResource = Convert.ToBase64String(bytes);
            OnPropertyChanged("ImageSrc");
        }

        protected void OnPropertyChanged(string propName) {
            PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propName));
        }
    }
}